    type      = "CODEPIPELINE"
    buildspec = "buildspec.yml"
  }

  logs_config {
    cloudwatch_logs {
      group_name  = "/codebuild/${var.environment}-${each.value.name}"
      stream_name = "build-log"
    }
  }
