  load_balancer {
    target_group_arn = aws_lb_target_group.public[each.key].arn
    container_name   = each.value.name
    container_port   = each.value.container_port
  }

  lifecycle {
    ignore_changes = [desired_count]
  }

  tags = {
    Name = "${var.environment}-${each.value.name}-service"
  }
}

