resource "aws_security_group" "ecs" {
  name        = "${var.environment}-ecs-sg"
  description = "Security group for ECS services"
  vpc_id      = var.vpc_id

  ingress {
    from_port       = 0
    to_port         = 0
    protocol        = "-1"
    security_groups = [aws_security_group.api_gateway.id]
    description     = "Allow all traffic from API Gateway"
  }

  ingress {
    from_port       = 0
    to_port         = 0
